/** @format */

import { useState } from "react";
import { Container, Form, FormGroup, Label, Input } from "reactstrap";
import { updateItem } from "../InventoryUtils";
import { Link, withRouter } from "react-router-dom";

const ItemEdit = ({ propItem }) => {
  const [item, setItem] = useState(propItem);
  const title = (
    <h2>{item.id ? "Edit Inventory Item" : "Add Inventory Item"}</h2>
  );

  const handleSubmit = (event) => {
    event.preventDefault();
    updateItem(item.id, item).then((response) => {
      this.props.history.push("/");
    });
  };

  const handleChange = (event) => {
    const target = event.target;
    const value = event.value;
    const name = target.name;
    let updatedItem = { ...item };
    updatedItem[name] = value;
    setItem(updatedItem);
  };
  return (
    <div>
      <Container>
        {title}
        <Form onSubmit={handleSubmit}>
          <FormGroup>
            <Label for="productName">Name</Label>
            <Input
              type="text"
              name="productName"
              id="productName"
              value={item.productName || ""}
              onChange={handleChange}
              autoComplete="productName"
            />
          </FormGroup>
          <FormGroup>
            <Label for="quantity">Name</Label>
            <Input
              type="number"
              name="quantity"
              id="quantity"
              value={item.quantity || ""}
              onChange={handleChange}
              autoComplete="quantity"
            />
          </FormGroup>
          <FormGroup>
            <Label for="price">Name</Label>
            <Input
              type="number"
              name="price"
              id="price"
              value={item.price || ""}
              onChange={handleChange}
              autoComplete="price"
            />
          </FormGroup>
          <FormGroup>
            <Label for="createdAt">Name</Label>
            <Input
              type="text"
              name="createdAt"
              id="createdAt"
              value={item.createdAt || ""}
              onChange={handleChange}
              autoComplete="createdAt"
            />
          </FormGroup>
        </Form>
      </Container>
    </div>
  );
};

export default withRouter(ItemEdit);
