/** @format */

import { useEffect, useState } from "react";
import { ButtonGroup, Button, Table } from "reactstrap";
import { getItems, deleteItem } from "../InventoryUtils";
import { Link } from "react-router-dom";
import AppNavBar from "./AppNavBar";

const ItemsList = () => {
  const [items, setItems] = new useState([]);

  useEffect(() => {
    fetchItems();
  }, []);

  const fetchItems = () => {
    getItems().then((response) => {
      setItems(response.data);
    });
  };

  const removeItem = (id) => {
    deleteItem(id).then((response) => {
      let updatedItems = [...this.items].filter((item) => item.id != id);
      setItems(updatedItems);
    });
  };

  return (
    <div>
      <AppNavBar />
      {items.map((item) => (
        <Table>
          {console.log(item)}
          <tr>
            <td>{item.productName}</td>
            <td>{item.quantity}</td>
            <td>{item.price}</td>
            <td>{item.createdAt}</td>
            <td>
              <ButtonGroup>
                <Button
                  size="sm"
                  color="primary"
                  tag={Link}
                  to={"/inventory/items/" + item.id}
                >
                  Edit
                </Button>
                <Button
                  size="sm"
                  color="danger"
                  onClick={() => this.remove(item.id)}
                >
                  Delete
                </Button>
              </ButtonGroup>
            </td>
          </tr>
        </Table>
      ))}
    </div>
  );
};

export default ItemsList;
